S.<\\/h4><\\/li><\\/ul>\"},\"elements\":[],\"widgetType\":\"text-editor\"},{\"id\":\"2d2c905\",\"elType\":\"widget\",\"settings\":{\"carousel\":[{\"id\":456,\"url\":\"http:\\/\\/157.245.125.52\\/wp-blog-2019-2\\/wp-content\\/uploads\\/2019\\/11\\/60271189_345986636061175_8757844032842819197_n.jpg\"},{\"id\":457,\"url\":\"http:\\/\\/157.245.125.52\\/wp-blog-2019-2\\/wp-content\\/uploads\\/2019\\/11\\/52397807_1943351969119995_3601019194814865879_n.jpg\"},{\"id\":459,\"url\":\"http:\\/\\/157.245.125.52\\/wp-blog-2019-2\\/wp-content\\/uploads\\/2019\\/11\\/34982521_195318671123651_623750867673153536_n1.jpg\"},{\"id\":460,\"url\":\"http:\\/\\/157.245.125.52\\/wp-blog-2019-2\\/wp-content\\/uploads\\/2019\\/11\\/72713114_2770586609652257_681063701544960000_o.jpg\"},{\"id\":461,\"url\":\"http:\\/\\/157.245.125.52\\/wp-blog-2019-2\\/wp-content\\/uploads\\/2019\\/11\\/59910745_2468906269820294_4483006749539827712_o.jpg\"},{\"id\":462,\"url\":\"http:\\/\\/157.245.125.52\\/wp-blog-2019-2\\/wp-content\\/uploads\\/2019\\/11\\/26198432_401872830252827_1145057999813404045_o.jpg\"}],\"thumbnail_size\":\"intrinsic-portfolio-x-x\",\"slides_to_show\":\"3\",\"autoplay_speed\":5008,\"direction\":\"rtl\"},\"elements\":[],\"widgetType\":\"image-carousel\"}],\"isInner\":false}],\"isInner\":false}]'),
